import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  LinearProgress,
  Alert,
} from '@mui/material';
import { studyActivitiesAPI } from '../services/api';

// Activity card images
const activityImages = {
  'Flashcards': '/images/flashcards.jpg',
  'Multiple Choice': '/images/quiz.jpg',
  'Writing Practice': '/images/writing.jpg',
  'Vocabulary Quiz': '/images/vocabulary.jpg'
};

// Default image if activity image is not found
const defaultImage = 'https://via.placeholder.com/300x140?text=Study+Activity';

function StudyActivities() {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;

    const fetchActivities = async () => {
      try {
        const response = await studyActivitiesAPI.getAll();
        if (mounted) {
          setActivities(response.data || []);
          setError(null);
        }
      } catch (error) {
        if (mounted) {
          console.error('Error fetching study activities:', error);
          setError('Failed to load study activities. Please try again later.');
          setActivities([]);
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    fetchActivities();

    return () => {
      mounted = false;
    };
  }, []); // Empty dependency array means this effect runs once on mount

  const getActivityImage = (activity) => {
    // Try to get the image from our mapping
    if (activityImages[activity.name]) {
      return activityImages[activity.name];
    }
    // Fallback to the activity's thumbnail_url if it exists
    if (activity.thumbnail_url && activity.thumbnail_url !== 'https://example.com/thumbnail.jpg') {
      return activity.thumbnail_url;
    }
    // Use default image as last resort
    return defaultImage;
  };

  if (loading) {
    return <LinearProgress />;
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mt: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!activities.length) {
    return (
      <Alert severity="info" sx={{ mt: 2 }}>
        No study activities available.
      </Alert>
    );
  }

  return (
    <Grid container spacing={3}>
      {activities.map((activity) => (
        <Grid item xs={12} sm={6} md={4} key={activity.id}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={getActivityImage(activity)}
              alt={activity.name}
              onError={(e) => {
                if (e.target.src !== defaultImage) {
                  e.target.src = defaultImage;
                }
              }}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {activity.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {activity.description}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" component={RouterLink} to={`/study/${activity.id}`}>
                LAUNCH
              </Button>
              <Button size="small" component={RouterLink} to={`/study/${activity.id}/details`}>
                VIEW DETAILS
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default StudyActivities;
