import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Grid,
  Card,
  CardContent,
  CardActions,
  Typography,
  Button,
  LinearProgress,
  Alert,
  Container,
  Box,
} from '@mui/material';
import { studyActivitiesAPI } from '../services/api';

function StudyActivities() {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;

    const fetchActivities = async () => {
      try {
        const response = await studyActivitiesAPI.getAll();
        if (mounted) {
          setActivities(response.data || []);
          setError(null);
        }
      } catch (error) {
        if (mounted) {
          console.error('Error fetching study activities:', error);
          setError('Failed to load study activities. Please try again later.');
          setActivities([]);
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    fetchActivities();

    return () => {
      mounted = false;
    };
  }, []);

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <LinearProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }

  if (!activities.length) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Alert severity="info">No study activities available.</Alert>
      </Container>
    );
  }

  const getActivityColor = (name) => {
    const colors = {
      'Flashcards': '#bbdefb',
      'Multiple Choice': '#c8e6c9',
      'Writing Practice': '#ffcdd2'
    };
    return colors[name] || '#f5f5f5';
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={4}>
        {activities.map((activity) => (
          <Grid item xs={12} sm={6} md={4} key={activity.id}>
            <Card 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: 4,
                },
              }}
            >
              <Box 
                sx={{ 
                  height: 140, 
                  bgcolor: getActivityColor(activity.name),
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  p: 2,
                  textAlign: 'center'
                }}
              >
                <Typography variant="h4" component="h2" color="text.secondary">
                  {activity.name}
                </Typography>
              </Box>
              <CardContent sx={{ flexGrow: 1, pb: 1 }}>
                <Typography variant="body2" color="text.secondary">
                  {activity.description}
                </Typography>
              </CardContent>
              <CardActions sx={{ p: 2, pt: 0 }}>
                <Button
                  variant="contained"
                  color="primary"
                  size="medium"
                  component={RouterLink}
                  to={`/study/${activity.id}`}
                  sx={{ mr: 1 }}
                >
                  LAUNCH
                </Button>
                <Button
                  variant="outlined"
                  size="medium"
                  component={RouterLink}
                  to={`/study/${activity.id}/details`}
                >
                  VIEW DETAILS
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default StudyActivities;
