import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  LinearProgress,
  Alert,
  Box,
} from '@mui/material';
import { studyActivitiesAPI } from '../services/api';

// Activity images with proper paths
const activityImages = {
  'Flashcards': '/static/images/activities/flashcards.jpg',
  'Multiple Choice': '/static/images/activities/quiz.jpg',
  'Writing Practice': '/static/images/activities/writing.jpg'
};

// Default image with proper styling
const defaultImage = 'data:image/svg+xml;base64,';

function StudyActivities() {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;

    const fetchActivities = async () => {
      try {
        const response = await studyActivitiesAPI.getAll();
        if (mounted) {
          setActivities(response.data || []);
          setError(null);
        }
      } catch (error) {
        if (mounted) {
          console.error('Error fetching study activities:', error);
          setError('Failed to load study activities. Please try again later.');
          setActivities([]);
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    fetchActivities();

    return () => {
      mounted = false;
    };
  }, []);

  if (loading) {
    return <LinearProgress />;
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ mt: 2 }}>
        {error}
      </Alert>
    );
  }

  if (!activities.length) {
    return (
      <Alert severity="info" sx={{ mt: 2 }}>
        No study activities available.
      </Alert>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Grid container spacing={4}>
        {activities.map((activity) => (
          <Grid item xs={12} sm={6} md={4} key={activity.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="140"
                image={activityImages[activity.name] || defaultImage}
                alt={activity.name}
                sx={{
                  objectFit: 'cover',
                  backgroundColor: '#f5f5f5'
                }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h5" component="h2">
                  {activity.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {activity.description}
                </Typography>
              </CardContent>
              <CardActions sx={{ p: 2, pt: 0 }}>
                <Button 
                  size="small" 
                  variant="contained" 
                  component={RouterLink} 
                  to={`/study/${activity.id}`}
                  sx={{ mr: 1 }}
                >
                  LAUNCH
                </Button>
                <Button 
                  size="small"
                  variant="outlined"
                  component={RouterLink} 
                  to={`/study/${activity.id}/details`}
                >
                  VIEW DETAILS
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default StudyActivities;
