import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  LinearProgress,
} from '@mui/material';
import { studyActivitiesAPI } from '../services/api';

function StudyActivities() {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchActivities = async () => {
      try {
        const response = await studyActivitiesAPI.getAll();
        setActivities(response.data);
      } catch (error) {
        console.error('Error fetching study activities:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchActivities();
  }, []);

  if (loading) {
    return <LinearProgress />;
  }

  return (
    <Grid container spacing={3}>
      {activities.map((activity) => (
        <Grid item xs={12} sm={6} md={4} key={activity.id}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={activity.thumbnail || 'https://via.placeholder.com/300x140'}
              alt={activity.name}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {activity.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {activity.description}
              </Typography>
            </CardContent>
            <CardActions>
              <Button
                size="small"
                component={RouterLink}
                to={`/study_activities/${activity.id}/launch`}
              >
                Launch
              </Button>
              <Button
                size="small"
                component={RouterLink}
                to={`/study_activities/${activity.id}`}
              >
                View Details
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}

export default StudyActivities;
